pair A, B, C, P;
pair[] G, M;

A = (1,3);
B = (0,0);
C = (4,0);
P = (2,1);
G[1] = (P + B + C)/3;
G[2] = (P + C + A)/3;
G[3] = (P + A + B)/3;
M[1] = (P + A)/2;
M[2] = (P + B)/2;
M[3] = (P + C)/2;

draw(A--B--C--cycle);
draw(A--P);
draw(B--P);
